import "./App.css";
import Menu from "./Assets/Components/Menu";
import Formulario from "./Assets/Components/Formulario/index";
import UseForm from "./Assets/Components/UseForm";
import ReviewForm from "./Assets/Components/ReviewForm";
import Thenks from "./Assets/Components/Thenks";

// Hooks
import { useForm } from "./hooks/useForm";

function App() {
  const formComponents = [<UseForm />, <ReviewForm />, <Thenks />];

  const { currentStep, currentComponents, changeStep, isLastStep } =
    useForm(formComponents);

  return (
    <div className="app">
      <Menu />
      <Formulario />
      <UseForm />
      <ReviewForm />
      <Thenks />
      <useForm />
      <form onSubmit={(e) => changeStep(currentStep + 1, e)}>
      <div className="inputs-conteiner">{currentComponents}</div>
      <div className="actions">
        <button type="button" onClick={() => changeStep(currentStep - 1)}>
          <span>Voltar</span>
        </button>
        {!isLastStep ? (
          <button type="submit">
            <span>Avan√ßar</span>
          </button>
        ) : (
          <button type="button">
            <span>Enviar</span>
          </button>
        )}
        ;
      </div>
    </form>
    </div>
  );
}

export default App;
