//Components
import { GrFormNext, GrFormPrevious} from "react-icons/gr";
import './App.css';


function App() {
  return (
    <div className='app'>
     <div className='header'>
     <h2>Deixe sua avaliação</h2>
     <span>Ficamos felizes com a sua compra, utilize o formulário abaixo para avaliar o produto</span>
     </div>
     <div className='form-container'>
     <span>etapas</span>
     <form>
      <div className='actions'>
      <button type='button'>
        <GrFormPrevious />
        <span>Voltar</span>
        </button>
      <button type='submit'>
        <GrFormNext />
        <span>Avançar</span>
      </button>
      </div>
     </form>
     </div>
    </div>
  );
}

export default App;
