import React from "react";
import "./styles.css";

export default function Formulario({
  changeStep,
  currentStep,
  currentComponents,
  isLastStep
}) {
  return (
    <div className="form-container">
      <span>etapas</span>

      <form onSubmit={(e) => changeStep(currentStep + 1, e)}>
      <div className="inputs-conteiner">{currentComponents[currentStep]}</div>
        <div className="actions">
          <button type="button" onClick={() => changeStep(currentStep - 1)}>
            <span>Voltar</span>
          </button>
          {!isLastStep ? (
            <button type="submit">
              <span>Avan√ßar</span>
            </button>
          ) : (
            <button type="button">
              <span>Enviar</span>
            </button>
          )}
          ;
        </div>
      </form>
    </div>
  );
}
